use std::io::{self,Read};fn D(n:i64)->bool{let s=n.to_string();s.len()%2==0&&s[..s.len()/2]==s[s.len()/2.. ]}fn R(n:i64)->bool{let s=n.to_string();(1..s.len()).any(|k|s.len()%k==0&&s[..k].repeat(s.len()/k)==s)}fn main(){let mut i=String::new();io::stdin().read_to_string(&mut i).unwrap();let d:String=i.chars().filter(|&c|c!='\n').collect();let(mut a,mut b)=(0i64,0i64);for r in d.split(','){if r.trim().is_empty(){continue}let p: Vec<i64>=r.split('-').map(|x|x.parse().unwrap()).collect();for n in p[0]..=p[1]{if D(n){a+=n}if R(n){b+=n}}}println!("{}\n{}",a,b)}
